public virtual System.Int32 LastTouchedInDays
{
    get
    {
        int days = 0;
        DateTime curDataTime = DateTime.UtcNow;
        DateTime lastTouched;

        try
        {
            if (this.LastHistoryDate != null)
            {
                lastTouched = (DateTime)this.LastHistoryDate;
                TimeSpan timeSpan = new TimeSpan(curDataTime.Ticks - lastTouched.Ticks);
                days = (int)timeSpan.TotalDays;
            }
            else
            {
                days = 0;
            }
        }
        catch
        {
            days = 0;
        }
        return days;
    }
}

public virtual System.Int32 NumberOfOpenTickets
{
    get
    {
        int count = 0;
        try
        {
            Sage.Platform.Repository.IRepository<Sage.Entity.Interfaces.ITicket> tickets = Sage.Platform.EntityFactory.GetRepository<Sage.Entity.Interfaces.ITicket>();
            Sage.Platform.Repository.IQueryable query = (Sage.Platform.Repository.IQueryable)tickets;
            Sage.Platform.Repository.IExpressionFactory expression = query.GetExpressionFactory();
            Sage.Platform.Repository.IProjections projection = query.GetProjectionsFactory();
            Sage.Platform.Repository.ICriteria criteria = query.CreateCriteria();
            criteria.SetProjection(projection.Count("Account.Id"));

            criteria.Add(expression.And(
                expression.Eq("Account.Id", this.Id),
                expression.IsNull("CompletedDate")));
            count = (int)criteria.List<object>()[0];
        }
        catch (Exception ex)
        {
            throw new NotImplementedException("NumberOfOpenTickets", ex);
        }
        return count;
    }
}

public virtual System.Int32 NumberOfTotalTickets
{
    get
    {
        try
        {
            return Sage.Platform.EntityFactory.GetRepository<Sage.Entity.Interfaces.ITicket>().FindByProperty("Account.Id", this.Id).Count;
        }
        catch (Exception ex)
        {
            throw new NotImplementedException("NumberOfTotalTickets", ex);
        }
    }
}

