Sage.Entity.Interfaces.ITicket ticket = (Sage.Entity.Interfaces.ITicket)this.BindingSource.Current;
cmdPunchInOut.Enabled = true;
string punchedInUser = ticket.GetOtherUserPunchedIn();
if (!String.IsNullOrEmpty(punchedInUser))
{
    cmdPunchInOut.Enabled = false;
    cmdPunchInOut.Text = GetLocalResourceObject("PunchOut_DisplayName").ToString();
    if (DialogService != null)
    {
        DialogService.ShowMessage(String.Format(GetLocalResourceObject("OtherUserPunchedIn_Message").ToString(), ticket.TicketNumber, punchedInUser));
    }
}
else if (ticket.IsTicketPunchedIn())
{
    cmdPunchInOut.Text = GetLocalResourceObject("PunchOut_DisplayName").ToString();
}
else
{
    cmdPunchInOut.Text = GetLocalResourceObject("PunchIn_DisplayName").ToString();
}

    Sage.Entity.Interfaces.ITicket ticket = this.BindingSource.Current as Sage.Entity.Interfaces.ITicket;
    Sage.Entity.Interfaces.ITicketActivity ticketActivity = Sage.Platform.Orm.DynamicMethodLibraryHelper.Instance.Execute("Ticket.GetOpenTimedTicketActivity", ticket) as Sage.Entity.Interfaces.ITicketActivity;
    if (ticketActivity != null)
    {
        if (Convert.ToBoolean(Sage.Platform.Orm.DynamicMethodLibraryHelper.Instance.Execute("Ticket.IsDisplayActivityOnPunchOut", ticket)))
        {
			ticket.CompletedDate = DateTime.UtcNow;
            Sage.SalesLogix.Security.User UserContext = Sage.SalesLogix.BusinessRules.BusinessRuleHelper.GetCurrentUser();
            ticket.CompletedBy = UserContext.DefaultOwner;
			Response.Redirect(string.Format("TicketActivity.aspx?entityId={0}", (ticketActivity as Sage.Platform.ComponentModel.IComponentReference).Id));
        }
        else
        {
            Sage.Platform.Orm.DynamicMethodLibraryHelper.Instance.Execute("Ticket.PunchInOut", ticket);
            cmdPunchInOut.Text = GetLocalResourceObject("PunchIn_DisplayName").ToString();
        }
    }
    else
    {
        Sage.Platform.Orm.DynamicMethodLibraryHelper.Instance.Execute("Ticket.PunchInOut", ticket);
        cmdPunchInOut.Text = GetLocalResourceObject("PunchOut_DisplayName").ToString();
    }
    Sage.Platform.WebPortal.Services.IPanelRefreshService refresh = PageWorkItem.Services.Get<Sage.Platform.WebPortal.Services.IPanelRefreshService>();
    refresh.RefreshTabWorkspace();
